package ch.cloud.order.controller;import ch.cloud.common.api.user.result.UserServiceAPI;import ch.cloud.common.api.user.support.UserReq;import ch.cloud.common.common.tool.Tool;import com.alibaba.fastjson.JSON;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;import java.applet.AppletContext;@RestControllerpublic class IndexController {    private Logger logger = LoggerFactory.getLogger(IndexController.class.getName());    @Autowired    private UserServiceAPI userServiceAPI;    @RequestMapping(value = "/")    @ResponseBody    public String index() {        RestTemplate restTemplate = new RestTemplate();        String userStr = restTemplate.getForObject("http://localhost:8877/", String.class);        return "hello this is orderService + userStr：" + userStr;    }    @RequestMapping(value = "/hello")    public String helloFeign() {        String name = "chen";        name = userServiceAPI.helloFeign(name);        return  name;    }    @RequestMapping(value = "/helloAlan")    @ResponseBody    public String helloAlan() {        UserReq userReq = new UserReq();        userReq.setId(Tool.uuid());        userReq.setName("Alan");        userReq.setGender("男");        logger.info(">>>> 之前 >>>> " + System.identityHashCode(userReq));        String userReqStr = userServiceAPI.helloAlan(userReq);        logger.info(">>>> 之后 >>>> " + System.identityHashCode(userReq));        return JSON.toJSONString(userReq) + userReqStr;    }}