package ch.cloud.user.controller;import ch.cloud.common.api.user.support.UserReq;import ch.cloud.common.common.tool.Tool;import ch.cloud.user.service.IndexService;import com.alibaba.fastjson.JSON;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class IndexController {    private Logger logger = LoggerFactory.getLogger(IndexController.class.getName());    @Autowired    private IndexService indexService;    @RequestMapping(value = "/")    public String index() {        return "hello this is userService";    }    @RequestMapping(value = "/hello")    @ResponseBody    public String helloFeign(String name) {        return "hello Feign" + name;    }    @RequestMapping(value = "helloAlan")    @ResponseBody    public String helloAlan(@RequestBody UserReq userReq) {        logger.info(">>>> 之前入参 >>>> " + JSON.toJSONString(userReq));        logger.info(">>>> 入参 >>>> " + System.identityHashCode(userReq));        userReq.setId(Tool.uuid());        userReq.setName("chen");        userReq.setGender("女");        logger.info(">>>> 之前 >>>> " + System.identityHashCode(userReq));        int ii = 10;        logger.info(">>>> ii >>>> " + System.identityHashCode(ii));        String a = setUser(userReq, ii);        logger.info(">>>> ii >>>> " + ii);        logger.info(">>>> ii之后 >>>> " + System.identityHashCode(ii));        logger.info(">>>> 之后入参 >>>> " + JSON.toJSONString(userReq) );        logger.info(">>>> 之后 >>>> " + System.identityHashCode(userReq));        return a;    }    private String setUser(UserReq userReq, int ii) {        logger.info(">>>> 另一方法 >>>> " + System.identityHashCode(userReq) + "_" + System.identityHashCode(ii));        userReq.setId(Tool.uuid());        userReq.setName("chenyanyu");        userReq.setGender("1");        indexService.helloAlan(userReq);        ii = 20;        logger.info(">>>> 新的ii >>>> " + ii);        logger.info(">>>> 新的ii >>>> " + System.identityHashCode(ii));        return JSON.toJSONString(userReq);    }}